import React from "react";
import './App.css';

let value=0;

function inc(){
    value+=1;
    return value
}

const App = () => {
    return <>
        <div className="counter">
            <button className="btn btn-danger">-</button>
            <h2>The Value:{value}</h2>
            <button className="btn btn-primary">+</button>
        </div>
    </>;
}
export default App;
